jQuery(document).ready(function(){
    this.afwFormChange = false;
    jQuery('.afwInput').on( 'change', function(){
        this.afwFormChange = true;
        //alert('this.afwFormChange=' + this.afwFormChange);
    })
    jQuery('.afwButton').on( 'click', function(){
        this.afwFormChange = false;
        //alert('this.afwFormChange=' + this.afwFormChange);
    })
    jQuery(window).on('hashchange', function(e){
        alert('this.afwFormChange=' + this.afwFormChange);
    });
    jQuery(window).bind('beforeunload', function() {
        alert('this.afwFormChange=' + this.afwFormChange);
        if (this.afwFormChange==true) {
            return 'You may have changes on this page. Are you sure?';
        }
        // do something, preferably ajax request etc
        //return 'are you sure?';
    });
})

